mapper.select:
SELECT dorm.*, bed.`BED_CODE`, stu.`STUDENT_NO`, stu.`NAME`, stu.`NATION`, stu.`ID` AS STU_ID, coll.`name` AS COLLEGE_NAME, major.`name` AS MAJOR_NAME FROM dormitory_bed_stu t, student_info stu, sys_bed bed, sys_dorm dorm, sys_major major, sys_college coll WHERE t.`BED_ID` = bed.`ID` AND t.`STUDENT_ID` = stu.`ID` AND dorm.`ID`=bed.`DORM_ID` AND major.`id`=stu.`MAJOR_ID` AND coll.id=stu.`college_ID`
AND stu.`ID`=#{param.stuId,jdbcType=VARCHAR}
AND stu.`STUDENT_NO` =#{param.stuNo,jdbcType=VARCHAR}
AND stu.`NAME` LIKE '%${param.stuName}%'
